zero  = undefined :: Zero
one   = undefined :: One
two   = undefined :: Two
three = undefined :: Three
four  = undefined :: Four


-- addition, a la Prolog

class Add a b c | a b -&gt; c where
  add :: a -&gt; b -&gt; c

instance              Add  Zero    b  b
instance Add a b c =&gt; Add (Succ a) b (Succ c)


-- multiplication, a la Prolog

