  mul :: a -&gt; b -&gt; c

instance                           Mul  Zero    b Zero
instance (Mul a b c, Add b c d) =&gt; Mul (Succ a) b d


-- factorial, a la Prolog

class Fac a b | a -&gt; b where
  fac :: a -&gt; b

instance                                Fac  Zero    One
instance (Fac n k, Mul (Succ n) k m) =&gt; Fac (Succ n) m

